import React, { 
    // useState 
} from 'react';
import {
    // Drawer
    Box,
    Button,
    Typography
} from '@mui/material';

const aboutButtonStyle = {
    width: '100%',
    display: 'block',
    color: 'white',
    backgroundColor: 'rgb(10, 33, 77)', //primary - doesn't want to import
    fontSize: '1.5rem',
    fontFamily: 'sans-serif',
    fontWeight: '600',
    textTransform: 'none',
    borderRadius: 0,
    padding: '1.6rem',
    border: 'solid rgb(0, 0, 0) 3px',
        '&:hover': {
            backgroundColor: 'rgb(10, 33, 77)',
            border: 'solid white 3px',
            transition: '.3s'   
        },
}

const aboutContentStyle = {
    display: 'none'
}

const About = () => {

    // const [about, setAbout] = useState(false);

    const handleClick = () => {
        aboutContentStyle.display = 'block';
    }

    return (
        <Box
            // sx={{
            //     border:'solid black .30rem', 
            // }}
        >
            <Button
                sx={aboutButtonStyle}
                onClick={handleClick}
            >
                About Me
            </Button>
            <Box
                sx={aboutContentStyle}
            >
                <Typography
                    // sx={aboutContentStyle}
                >
                    This is a sample text with not much else going on
                </Typography>
            </Box>
            
        </Box>
    );
};

export default About;
