import { 
    // Box,
    Grid, 
    Link, 
    // ListItem, 
    // Typography 
} from '@mui/material';
// import { Container } from '@mui/system';
import React from 'react';



const works = [
    {
        name: 'project1',
        path: 'https://samouchka1.github.io/model-site/index.html',
        thumbnail: '/project-images/template-site-bg.png' //image
    },
    {
        name: 'project2',
        path: '',
        thumbnail: {} //image
    },
    {
        name: 'project3',
        path: '',
        thumbnail: {} //image
    },
    {
        name: 'project4',
        path: '',
        thumbnail: {}  //image
    },
    {
        name: 'project5',
        path: '',
        thumbnail: {}  //image
    }
]

const Projects = () => {

const gridStyle = {
    display: 'flex',
    justifyContent: 'center',
}

const gridItemStyle = {
    width: '20%',
    // padding: '6rem',
    // backgroundSize: 'cover',
    // backgroundPositionY: '40%',
    textAlign: 'center',
    color: 'white',
    opacity: '.9',
        '&:hover': {
            opacity: '1',
            animationName: 'zoom',
            animationDuration: '1s',
            animationFillMode: 'forwards'
        },

    border: 'solid red 3px' //test
}

return (

    <Grid container 
        sx={gridStyle}
    >
        {works.map((work) => (
            <Grid item xs={8} md={4}
                key={work}
                sx={gridItemStyle}
            >
                <img 
                    src={process.env.PUBLIC_URL + work.thumbnail} 
                    alt={work.name}
                    // sx={{backgroundSize: 'cover'}}
                />
                    <Link href={work.path}>
                        {work.name}
                    </Link>
            </Grid>
            ))}

    </Grid>  
)
}

export default Projects;
