import React from 'react';
import {
  AppBar,
  Toolbar, 
  Typography,
  List,
  ListItem,
  Link
} from '@mui/material';
import { 
    // grey
} from '@mui/material/colors';
import { Container } from '@mui/system';

const pages = [
  {
    name: 'About',
    path: '/about'
  },
  {
    name: 'My Work',
    path: '/projects'
  },
  {
    name: 'Contact',
    path: '/contact'
  }
]

//===STYLES===

const navbar = {
  display: 'flex',
  flexDirection: 'row',
  alignItems:'center',
  justifyContent: {
    md: 'right',
    xs: 'center'
  },
  height: {
    md: 108,
    xs: 75
  },
  backgroundColor : 'primary',
  width: '100%',
  margin: '1rem 0 1rem 0'
}

const navLinks = {
  fontWeight: '600',
  textDecoration: 'none',
  color: 'white',
  fontSize : {
    xs: '1.1rem',
    md: '1.3rem'
  }
}

const navLinksBorder = {
  padding: '2.2rem .5rem 2.2rem .5rem', //edit for xs
  border: 'solid rgba(0, 0, 0, 0) 3px',
    '&:hover': {
      border: 'solid white 3px',
      transition: '.3s'
    }
}

const NavBar = () => {

  return (
  <Container maxWidth={false} sx={{justifyContent: 'center'}}>
      <AppBar 
        position="relative"
        sx={navbar}
      >
        <Toolbar>
          <List sx={{ display: 'flex'}}>
            {pages.map((page) => (
                <ListItem key={page}>
                  <Typography noWrap sx={navLinksBorder}>
                    <Link 
                      href={page.path}
                      sx={navLinks}
                    >
                      {page.name}
                    </Link>
                  </Typography>
                </ListItem>
              ))}
          </List>
        </Toolbar>
      </AppBar>
    </Container>
  );
};
export default NavBar;
