import { 
    Grid, 
    Link, 
    // ListItem, 
    Typography 
} from '@mui/material';
import { Container } from '@mui/system';
import React from 'react';


const works = [
    {
        name: 'project1',
        path: '',
        thumbnail: './public/project-images/template-site-bg.png)' //image
    },
    {
        name: 'project2',
        path: '',
        thumbnail: {} //image
    },
    {
        name: 'project3',
        path: '',
        thumbnail: {} //image
    },
    {
        name: 'project4',
        path: '',
        thumbnail: {}  //image
    },
    {
        name: 'project5',
        path: '',
        thumbnail: {}  //image
    }
]

const Projects = () => {

const gridStyle = {
    display: 'flex',
    justifyContent: 'center',
    mx: 'auto'
}

const gridItemStyle = {
    padding: '6rem',
    backgroundSize: 'cover',
    backgroundPositionY: '40%',
    textAlign: 'center',
    color: 'white',
    opacity: '.9',

    border: 'solid red 3px' //test
}

return (

    <Grid container 
        sx={gridStyle}
    >
        {works.map((work) => (
            <Grid item xs={8} md={4}
                key={work}
                sx={gridItemStyle}
            >
                <Typography>
                    <Link 
                        href={work.path}
                    // backgroundImage: `url(${works.thumbnail})`,

                    >
                        {work.name}
                        {/* text over image? create component? */}
                    </Link>
                </Typography>
            </Grid>
            ))}

    </Grid>  
)
}

export default Projects;
