import React from 'react';
import {
  AppBar,
  Toolbar, 
  Typography,
  List,
  ListItem,
  Link,
  // useMediaQuery
} from '@mui/material';
// import { styled } from '@mui/material/styles';
import { 
    green, grey
} from '@mui/material/colors';
// import { useTheme } from '@mui/material/styles';

// const pages = ['About', 'Work', 'Contact'];
const pages = [
  {
    name: 'About',
    path: '/about'
  },
  {
    name: 'Work',
    path: '/work'
  },
  {
    name: 'Contact',
    path: '/contact'
  }
]

const responsiveNavbar = {
  mx: 'auto',
  display: 'flex',
  justifyContent: 'center',
  alignItems: 'left',
  backgroundColor : {
    xs: green[100],
    sm: green[300],
    md: green[500],
    lg: green[700]
  }
}

const responsiveNavLinks = {
  display: 'flex',
  flexDirection: 'row',
  textDecoration: 'none',
  '&:hover': {
    color: grey[300]
  },
  fontSize : {
    xs: 16,
    md: 32
  },
}

const NavBar = () => {

  return (
    <AppBar 
      position="static"
      elevation={5}
      sx={responsiveNavbar}
    >
      <Toolbar>
        <List
          sx={{
            display: 'flex',
            flexDirection: 'row',
            
          }}
        >
          {pages.map((page) => (
              <ListItem key={page}>
                <Typography
                  variant="h6" 
                  textAlign="center"

                >
                <Link 
                  href={page.path}
                  color='primary'
                  sx={responsiveNavLinks}
                >
                  {page.name}
                </Link>
                </Typography>
              </ListItem>
            ))}
        </List>
      </Toolbar>
    </AppBar>
  );
};
export default NavBar;
