import React from 'react';
import {
  AppBar,
  Toolbar, 
  Typography,
  List,
  ListItem,
  Link,
  // useMediaQuery
} from '@mui/material';
import { styled } from '@mui/material/styles';
import { 
    green
} from '@mui/material/colors';
// import { useTheme } from '@mui/material/styles';

// const pages = ['About', 'Work', 'Contact'];
const pages = [
  {
    name: 'About',
    path: '/about'
  },
  {
    name: 'Work',
    path: '/work'
  },
  {
    name: 'Contact',
    path: '/contact'
  }
]

const navbar = styled('Appbar')(({ theme }) => ({

  [theme.breakpoints.down('md')]: {
    // width: 'sm',
    backgroundColor : green[900]
  },
  [theme.breakpoints.up('md')]: {
    // width: 'md',
    backgroundColor : green[500]

  },
  [theme.breakpoints.up('lg')]: {
    // width: 'lg',
    backgroundColor : green[100]

  },
}));

const NavBar = () => {

  return (
    <AppBar 
      position="relative"
      elevation={5}
      sx={{
        height: '7rem',
        // background: 'rgb(121,171,189)',
        // background: 'linear-gradient(90deg, rgba(42,33,110,1) 0%, rgba(121,162,189,1) 100%)',
        backgroundColor: green[400],
        margin: '.5rem',
        maxWidth: 1000,
        mx: 'auto',
        borderRadius: 1,
        display: 'flex',
        justifyContent: 'center',
        alignItems: 'left'
      }}
    >
      <Toolbar>
        <List
          sx={{
            display: 'flex',
            flexDirection: 'row',
            
          }}
        >
          {pages.map((page) => (
              <ListItem key={page}>
                <Typography
                  variant="h6" 
                  textAlign="center"

                >
                <Link 
                  href={page.path}
                  color='primary'
                  sx={{
                    textDecoration: 'none'
                  }}
                >
                  {page.name}
                </Link>
                </Typography>
              </ListItem>
            ))}
        </List>
      </Toolbar>
    </AppBar>
  );
};
export default NavBar;
