import { 
    // Box,
    Grid, 
    Link,
    Typography, 
    // ListItem, 
    // Typography 
} from '@mui/material';
import { Box } from '@mui/system';
// import { Container } from '@mui/system';
import React from 'react';



const projects = [
    {
        name: 'Sample Site',
        path: 'https://samouchka1.github.io/model-site/index.html',
        thumbnail: 'template-site-bg.png' //image
    },
    {
        name: 'Dragon Theme Site',
        path: 'https://samouchka1.github.io/dragon-site/index.html',
        thumbnail: 'three-headed.jpg' //image
    },
    {
        name: 'PHP using \'includes\'',
        path: 'http://samouchka.great-site.net/index.php',
        thumbnail: 'php.jpg' //image
    },
    {
        name: 'PHP CRUD App',
        path: 'http://crudproject.great-site.net/index.php',
        thumbnail: 'php-crud.jpg'  //image
    },
    {
        name: 'PHP Login System',
        path: 'http://loginsystem.great-site.net/',
        thumbnail: 'php-login.jpg'  //image
    },
    {
        name: 'React To-do App',
        path: 'https://todo-list-react-12.netlify.app/',
        thumbnail: 'react-todo-bg.jpg'
    },
    {
        name: 'Wordpress Site',
        path: 'http://site2wordpress.iceiy.com/',
        thumbnail: 'wp-1.jpg'
    },
    {
        name: 'Github',
        path: 'https://github.com/samouchka1',
        thumbnail: 'github.jpg'
    }
]

const Projects = () => {

const gridStyle = {
    // display: 'flex',
    justifyContent: 'space-evenly',
    gap: '.2rem',
    boxSizing: 'unset'

}

const gridItemTextStyle = {
    position: 'relative',
    top: '50%',
    textAlign: 'center',
    fontSize: '1.5rem',
    fontWeight: '600',
    zIndex: '1'
}

// const gridItemImageStyle = {
    
// }

return (

    <Grid container sx={gridStyle}>
        {projects.map((project) => (

            <Grid item xs={8} md={3} key={project}>
                    <Typography sx={gridItemTextStyle}>
                        {project.name}
                    </Typography>
                    <Link href={project.path} target='_blank'>
                        <Box sx={{
                            backgroundImage: 'url(' + process.env.PUBLIC_URL + '/project-images/' + project.thumbnail + ')',
                            backgroundSize: 'cover',
                        
                            // width: '100%',
                            // height: 270,
                            opacity: '.9',
                            '&:hover': {
                                opacity: '1',
                                animationName: 'zoom',
                                animationDuration: '1s',
                                animationFillMode: 'forwards',
                                '@keyframes zoom' : {
                                    '0%': { transform: 'scale(1.0035)'},
                                    '100%' : { transform: 'scale(1.05)'}
                                }
                            }
                        }} />
                        {/* <Box
                            component="img" 
                            src={process.env.PUBLIC_URL + '/project-images/' + project.thumbnail} 
                            alt={project.name}
                            sx={gridItemImageStyle}
                        /> */}
                    </Link>
            </Grid>

        ))}

    </Grid>  
)
}

export default Projects;
