import React, { useState } from 'react';
import {
    Box,
    Button,
    Typography,
} from '@mui/material';

const aboutContentStyle = { 
    height: 0,    // param
    fontSize: '1.3rem',
    fontFamily: 'sans-serif',
    transition: '1s',
    flexDirection: 'row',
    margin: '.7rem 0 .7rem 0',
    backgroundColor: 'rgb(10, 33, 77)', //primary - doesn't want to import
    width: '100%',
}

const aboutButtonStyle = {
    position: 'relative',
    width: '100%',
    display: 'block', //param
    color: 'white',
    backgroundColor: 'rgb(10, 33, 77)', //primary - doesn't want to import
    fontSize: '1.5rem',
    fontFamily: 'sans-serif',
    fontWeight: '600',
    textTransform: 'none',
    borderRadius: 0,
    padding: '1.6rem',
    border: 'solid rgb(0, 0, 0) 3px',
        '&:hover': {
            backgroundColor: 'rgb(10, 33, 77)',
            border: 'solid white 3px',
            transition: '.3s'   
        },
}

const closeButtonStyle ={
    position: 'relative',
    top: '5%',
    left: '49%',
    display: 'none', //param
    border: 'solid rgb(255, 255, 255)',
    borderWidth: '0 5px 5px 0', //creates arrow
    transform: 'rotate(225deg)', //sets arrow direction
    padding: 2,
    color: 'white', //doesnt work
        '&:hover': {
            color: 'rgb(250, 12, 12)',//doesnt work
            borderWidth: '0 5px 5px 0'
        }
}

const About = () => {

    const [showAboutButton, setAboutButton] = useState(true)

    const AboutButton = () => {
        return (
            <Button 
                sx={aboutButtonStyle}
                onClick={setAboutButton(false)}
            >
                About Me
            </Button>
        )
    }
    
    // const CloseButton = () => {
    //     return (
    //         <Button sx={closeButtonStyle}>
    //         </Button>
    //     )
    // }

    return (
        <Box>
            {
                showAboutButton ? <AboutButton /> : null
            }
        </Box>
    );
};

export default About;
