import React from 'react';
import {
    Container, Typography
} from '@mui/material';
import bghero from './hero.jpg'

const heroStyle = {
    height: '78vh',
    width: '100vw',
    backgroundImage:`url(${bghero})`,
    backgroundSize: 'cover',
    borderTop:'solid black .35rem', //test
    borderBottom:'solid black .35rem' //test
}

const heroTextStyle = {
    position: 'relative',
    top: '33%',
    textAlign: 'center',
    fontSize: '2rem',
    fontFamily: 'Monda, sans-serif',
    color: 'rgb(185, 253, 121)'
}

const Hero = () => {
    return (
        <Container
            sx={heroStyle}
            maxWidth={false} //this takes off default width limit
        >
            <Typography
                component="h1"
                sx={heroTextStyle}
            >
                Greetings, I am Miguel.<br />{/*h1 */}
                Web Developer for Hire{/*p */}
            </Typography>
        </Container>
    )
}

export default Hero;
