import React from 'react';
import {
    Container, Typography
} from '@mui/material';
import welcome from './welcome.jpg';

const welcomeStyle = {
    height: '78vh',
    width: '100vw',
    backgroundImage:`url(${welcome})`,
    backgroundSize: 'cover',
    borderTop:'solid black .35rem', //test
    borderBottom:'solid black .35rem' //test
}

const welcomeTextStyle = {
    position: 'relative',
    top: '33%',
    textAlign: 'center',
    // fontSize: '2rem',
    fontFamily: 'Monda',
    color: 'rgb(185, 253, 121)',
    fontSize: {
        sm: '1.3rem'
    }
}

const Welcome = () => {
    return (
        <Container
            sx={welcomeStyle}
            maxWidth={false} //this takes off default width limit
        >
            <Typography
                variant="h3"
                sx={welcomeTextStyle}
            >
                Greetings, I am Miguel.<br />{/*h1 */}
                <Typography variant="h5" sx={{paddingTop: '2rem'}}>Web Developer for Hire</Typography>{/*p */}
            </Typography>
        </Container>
    )
}

export default Welcome;
