import React, { 
    // useState 
} from 'react';
import {
    // Drawer
    Box,
    Button,
    Typography,
} from '@mui/material';

const aboutContentStyle = { 
    // position: 'relative',
    height: 0,    // openAbout
    // display: 'flex',
    fontSize: '1.3rem',
    fontFamily: 'sans-serif',
    transition: '1s',
    flexDirection: 'row',
    margin: '.7rem 0 .7rem 0',
    backgroundColor: 'rgb(10, 33, 77)', //primary - doesn't want to import
    width: '100%',
}

const aboutButtonStyle = {
    position: 'relative',
    width: '100%',
    display: 'block',
    color: 'white',
    backgroundColor: 'rgb(10, 33, 77)', //primary - doesn't want to import
    fontSize: '1.5rem',
    fontFamily: 'sans-serif',
    fontWeight: '600',
    textTransform: 'none',
    borderRadius: 0,
    padding: '1.6rem',
    border: 'solid rgb(0, 0, 0) 3px',
        '&:hover': {
            backgroundColor: 'rgb(10, 33, 77)',
            border: 'solid white 3px',
            transition: '.3s'   
        },
}

const closeButtonStyle ={
    position: 'relative',
    top: '5%',
    left: '49%',
    display: 'none', //param
    border: 'solid rgb(255, 255, 255)',
    borderWidth: '0 5px 5px 0', //creates arrow
    transform: 'rotate(225deg)', //sets arrow direction
    padding: 2,
    color: 'white', //doesnt work
        '&:hover': {
            color: 'rgb(250, 12, 12)',//doesnt work
            borderWidth: '0 5px 5px 0'
        }
}



const About = () => {

    return (
        <Box sx={aboutContentStyle}>
            <Button sx={aboutButtonStyle}  
                onClick={()=>{
                    aboutContentStyle.height = 340;
                    aboutButtonStyle.display= 'none';
                    closeButtonStyle.display = 'block';
                }}
            > About Me
            </Button>

            <Button xs={closeButtonStyle}
                onClick={()=>{
                    aboutContentStyle.height = 0;
                    aboutButtonStyle.display = 'block';
                    closeButtonStyle.display = 'none';
                }}
            ></Button>

            <Typography>
                Thank you for stopping by!<br/> <br/> I like to develop functional, responsive websites. Strongly familiar with HTML, CSS, JavaScript and PHP. Experience with MySQL, MongoDB/Mongoose, Node/ExpressJS. Knowledge in ReactJS, PHP Laravel, WordPress and installing, working with frameworks and libraries. <br/>I'm always looking forward to a new project to
                tackle. Let me help your business grow! <br/> <br/>Professional and reliable.
            </Typography>
            
        </Box>
    );
};

export default About;
