import React from 'react';
import { 
    Grid, 
    Link,
    Box,
    Typography, 
} from '@mui/material';

const emails = [
    {
        name: 'Responsive Email',
        path: 'https://samouchka1.github.io/HTML-email-responsive/',
        thumbnail: ''
    },
    {
        name: 'Elite Charters',
        path: 'https://samouchka1.github.io/HTML-email-elite-charters/',
        thumbnail: ''
    },
    {
        name: 'Paypal Clone',
        path: 'https://samouchka1.github.io/HTML-email-paypal-clone/',
        thumbnail: ''
    },
    {
        name: 'GNC Clone',
        path: 'https://samouchka1.github.io/HTML-email-gnc-clone/',
        thumbnail: ''
    },
]

const Emails = () => {

    const gridStyle = {
        justifyContent: 'center',
        gap: '1.1rem'
    }
    
    const gridItemTextStyle = {
        position: 'relative',
        color: 'white',
        textShadow: '3px 3px 4px black',
        top: '88%',
        left: '3%',
        fontSize: '1.3rem',
        fontWeight: '600',
        zIndex: '1'
    }
    
    const gridItemImageStyle = {
        width: '100%',
        height: {
            md: 270,
            sm: 330,
            xs: 225
        },
        opacity: '.9',
        '&:hover': {
            opacity: '1',
            animationName: 'zoom',
            animationDuration: '1s',
            animationFillMode: 'forwards',
            '@keyframes zoom' : {
                '0%': { transform: 'scale(1.0035)'},
                '100%' : { transform: 'scale(1.05)'}
            }
        }
    }

    return (
        <Grid container sx={gridStyle} id="emails">
            {emails.map((email) => (
    
                <Grid item xs={8} md={3} key={email}>
                        <Typography sx={gridItemTextStyle}>
                            {email.name}
                        </Typography>
                        <Link href={email.path} target='_blank'>
