import React from 'react';
import {
  AppBar,
  Toolbar, 
  Typography,
  List,
  ListItem,
  Link
} from '@mui/material';
import { 
  // deepPurple,
  // green,
    // grey
} from '@mui/material/colors';

const pages = [
  {
    name: 'About',
    path: '/about'
  },
  {
    name: 'My Work',
    path: '/work'
  },
  {
    name: 'Contact',
    path: '/contact'
  }
]

const navbar = {
  display: 'flex',
  flexDirection: 'row',
  alignItems:'center',
  justifyContent: 'right',
  border:'solid black .35rem', //test
  borderTop: 'solid black .45rem', //test
  height: '17vh',
  backgroundColor : 'primary'
}

const navLinks = {
  fontWeight: '600',
  textDecoration: 'none',
  color: 'white',
  fontSize : {
    sm: '1.1rem',
    md: '1.3rem'
  }
}

const navLinksBorder = {
  padding: {
    sm: '2.3rem .5rem 2.1rem .5rem',
    md: '2.25rem 1rem 2.15rem 1rem'
  },
  margin: {
    sm: '0 .5rem 0 .5rem',
    md:'0 1rem 0 1rem',
  },
  border: 'solid rgb(10, 33, 77) 3px', //rgb(10, 33, 77) is primary
    '&:hover': {
      border: 'solid white 3px',
      transition: '.2s'
    }
}

const NavBar = () => {

  return (
    <AppBar 
      position="relative"
      elevation={3}
      sx={navbar}
    >
      <Toolbar>
        <List sx={{ display: 'flex'}}>
          {pages.map((page) => (
              <ListItem key={page}>
                <Typography noWrap sx={navLinksBorder}>
                  <Link 
                    href={page.path}
                    sx={navLinks}
                  >
                    {page.name}
                  </Link>
                </Typography>
              </ListItem>
            ))}
        </List>
      </Toolbar>
    </AppBar>
  );
};
export default NavBar;
