import React from 'react';
import {
  AppBar,
  Toolbar, 
  Typography,
  List,
  ListItem,
  Link,
  // useMediaQuery
} from '@mui/material';
// import { styled } from '@mui/material/styles';
import { 
    green, grey
} from '@mui/material/colors';
// import { useTheme } from '@mui/material/styles';

// const pages = ['About', 'Work', 'Contact'];
const pages = [
  {
    name: 'About',
    path: '/about'
  },
  {
    name: 'Work',
    path: '/work'
  },
  {
    name: 'Contact',
    path: '/contact'
  }
]

const responsiveNavbar = {
  backgroundColor : {
    xs: green[100],
    sm: green[300],
    md: green[500],
    lg: green[700]
  }
}

const NavBar = () => {

  return (
    <AppBar 
      position="relative"
      elevation={5}
      sx={{
        height: '7rem',
        // backgroundColor: green[400],
        responsiveNavbar,
        margin: '.5rem',
        // maxWidth: 1000,
        mx: 'auto',
        borderRadius: 1,
        display: 'flex',
        justifyContent: 'center',
        alignItems: 'left'
      }}
    >
      <Toolbar>
        <List
          sx={{
            display: 'flex',
            flexDirection: 'row',
            
          }}
        >
          {pages.map((page) => (
              <ListItem key={page}>
                <Typography
                  variant="h6" 
                  textAlign="center"

                >
                <Link 
                  href={page.path}
                  color='primary'
                  sx={{
                    textDecoration: 'none',
                    '&:hover': {
                      color: grey[300]
                    },
                  }}
                >
                  {page.name}
                </Link>
                </Typography>
              </ListItem>
            ))}
        </List>
      </Toolbar>
    </AppBar>
  );
};
export default NavBar;
