import React, { 
    useState 
} from 'react';
import {
    // Drawer
    Box,
    Button,
    // Typography,
} from '@mui/material';

const aboutButtonStyle = {      //BUTTON
    width: '100%',
    display: 'block',   //handleClick
    // display: 'none',     //<<<<param
    color: 'white',
    backgroundColor: 'rgb(10, 33, 77)', //primary - doesn't want to import
    fontSize: '1.5rem',
    fontFamily: 'sans-serif',
    fontWeight: '600',
    textTransform: 'none',
    borderRadius: 0,
    padding: '1.6rem',
    border: 'solid rgb(0, 0, 0) 3px',
        '&:hover': {
            backgroundColor: 'rgb(10, 33, 77)',
            border: 'solid white 3px',
            transition: '.3s'   
        },
}

const aboutContentStyle = {     //CONTENT
    // height: 0,   //    param
    height: '78vh',    // openAbout
    display: 'flex',
    fontSize: '1.3rem',
    fontFamily: 'sans-serif',
    // z-index: 1,
    transition: '.5s',
    flexDirection: 'row',
    margin: '.7rem 0 .7rem 0',
    backgroundColor: 'rgb(10, 33, 77)', //primary - doesn't want to import
    width: '100%'
}

const closeButtonStyle ={
    position: 'relative',
    top: '5%',
    left: '49%',
    width: 5,
    height: 5,
    display: 'block', /*param*/
    border: 'solid rgb(255, 255, 255)',
    borderWidth: '0 5px 5px ', /*creates arrow*/
    transform: 'rotate(225deg)', /*creates arrow (direction)*/
    backgroundColor: 'rgba(70, 70, 70, 0)',
    padding: 10
}



const About = () => {

    // const [showAboutContent, setShowAboutContent] = useState(false)
        // setShowAboutContent(true)

    const [showAbout, setShowAbout] = useState(true)
    const setAbout = () => setShowAbout(false)

    const [showCloseBtn, setCloseBtn] = useState(false)
    const setClose = () => setCloseBtn(true)

    const AboutButton =() => {
        return (
            <Button
                sx={aboutButtonStyle}
                onClick={setAbout}
            >
                About Me
            </Button>
        )
    }
    const CloseButton =() => {
        return (
            <Button 
                sx={closeButtonStyle} 
                onClick={setClose}
            />
        )
    }

    const AboutContent = () => {
        return (
            <Box
                // sx={}
            >
                <Typography>
                    Thank you for stopping by!<br/> <br/> I like to develop functional, responsive websites. Strongly familiar with HTML, CSS, JavaScript and PHP. Experience with MySQL, MongoDB/Mongoose, Node/ExpressJS. Knowledge in ReactJS, PHP Laravel, WordPress and installing, working with frameworks and libraries. <br/>I'm always looking forward to a new project to
                    tackle. Let me help your business grow! <br/> <br/>Professional and reliable.
                </Typography>

            </Box>
        )
    }


    return (
        <Box>
            {showCloseBtn ? <CloseButton />  : null}
            {/* {showAboutContent ? <AboutContent /> : null} */}
            {showAbout ? <AboutButton /> : null}
           
            <Box
                sx={aboutContentStyle}
            >
                This is sample text
            </Box>
            
        </Box>
    );
};

export default About;
