import { 
    // Box,
    Grid, 
    Link, 
    // ListItem, 
    // Typography 
} from '@mui/material';
// import { Container } from '@mui/system';
import React from 'react';



const works = [
    {
        name: 'project1',
        path: 'https://samouchka1.github.io/model-site/index.html',
        thumbnail: 'template-site-bg.png' //image
    },
    {
        name: 'project2',
        path: 'https://samouchka1.github.io/dragon-site/index.html',
        thumbnail: 'three-headed.jpg' //image
    },
    {
        name: 'project3',
        path: 'http://samouchka.great-site.net/index.php',
        thumbnail: 'php.jpg' //image
    },
    {
        name: 'project4',
        path: 'http://crudproject.great-site.net/index.php',
        thumbnail: 'php-crud.jpg'  //image
    },
    {
        name: 'project5',
        path: 'http://loginsystem.great-site.net/',
        thumbnail: 'php-login.jpg'  //image
    },
    {
        name: 'project6',
        path: 'https://todo-list-react-12.netlify.app/',
        thumbnail: 'react-todo-bg.jpg'
    },
    {
        name: 'wp-1',
        path: 'http://site2wordpress.iceiy.com/',
        thumbnail: 'wp-1.jpg'
    },
    {
        name: 'github',
        path: 'https://github.com/samouchka1',
        thumbnail: 'github.jpg'
    }
]

const Projects = () => {

const gridStyle = {
    display: 'flex',
    justifyContent: 'center',
}

// process.env.PUBLIC_URL + '/project-images/' + work.thumbnail

// const gridItemStyle = {
//     // background: `url(${thumbnails})`,
//     padding: '6rem',
//     backgroundSize: 'cover',
//     backgroundPositionY: '40%',
//     // textAlign: 'center',
//     color: 'white',
//     opacity: '.9',
//         '&:hover': {
//             opacity: '1',
//         },

// }

// const gridImageStyle = {
//     width: '2rem',
//     height: '3rem',
// }

return (

    <Grid container 
        sx={gridStyle}
    >
        {works.map((work) => (

            <Grid item xs={8} md={4}
                key={work}
                // sx={gridItemStyle}
                sx={{
                    backgroundImage: process.env.PUBLIC_URL + '/project-images/' + work.thumbnail,

                    width: '30vw',
                    height: '15%',
                    // padding: '6rem',
                    backgroundSize: 'cover',
                    // backgroundPositionY: '40%',
                    color: 'white',
                    opacity: '.9',
                        '&:hover': {
                            opacity: '1',
                        },

                    border: 'solid red 3px' //test
                    
                }}
            >
                <div>
                    <Link href={work.path}>
                        <img 
                            src={process.env.PUBLIC_URL + '/project-images/' + work.thumbnail} 
                            alt={work.name}
                        />
                        {work.name}
                    </Link>
                </div>
            </Grid>
            ))}

    </Grid>  
)
}

export default Projects;
