import React, { 
    // useState 
} from 'react';
import {
    // Drawer
    Box,
    Button,
    Typography
} from '@mui/material';

const aboutButtonStyle = {      //BUTTON
    width: '100%',
    display: 'block',   //handleClick
    // display: 'none',     <<<<param
    color: 'white',
    backgroundColor: 'rgb(10, 33, 77)', //primary - doesn't want to import
    fontSize: '1.5rem',
    fontFamily: 'sans-serif',
    fontWeight: '600',
    textTransform: 'none',
    borderRadius: 0,
    padding: '1.6rem',
    border: 'solid rgb(0, 0, 0) 3px',
        '&:hover': {
            backgroundColor: 'rgb(10, 33, 77)',
            border: 'solid white 3px',
            transition: '.3s'   
        },
}

const aboutContentStyle = {     //CONTENT
    height: 0,   //    param
    // height: '78vh',     openAbout
    display: 'flex',
    fontSize: '1.3rem',
    fontFamily: 'sans-serif',
    // z-index: 1,
    transition: '.5s',
    flexDirection: 'row',
    margin: '.7rem 0 .7rem 0',
    backgroundColor: 'rgb(10, 33, 77)', //primary - doesn't want to import
    width: '100%'
}

const About = () => {

    // const [about, setAbout] = useState(false);

    const openAbout = () => {
        aboutButtonStyle.display = 'none';
        aboutContentStyle.height = '78vh';
    }

    return (
        <Box
            // sx={{
            //     border:'solid black .30rem', 
            // }}
        >
            <Button
                sx={aboutButtonStyle}
                onClick={openAbout}
            >
                About Me
            </Button>
            <Box
                sx={aboutContentStyle}
            >
                {/* <Typography> */}
                    This is a sample text with not much else going on
                {/* </Typography> */}
            </Box>
            
        </Box>
    );
};

export default About;
