import React from 'react';
import {
  AppBar,
  Toolbar, 
  Typography,
  List,
  ListItem,
  Link,
  // useMediaQuery
} from '@mui/material';
// import { styled } from '@mui/material/styles';
import { 
    green
} from '@mui/material/colors';
// import { useTheme } from '@mui/material/styles';

// const pages = ['About', 'Work', 'Contact'];
const pages = [
  {
    name: 'About',
    path: '/about'
  },
  {
    name: 'Work',
    path: '/work'
  },
  {
    name: 'Contact',
    path: '/contact'
  }
]

// const Breakpoints = styled('Appbar')(({ theme }) => ({

//   [theme.breakpoints.down('md')]: {
//     backgroundColor : green[900]
//   },
//   [theme.breakpoints.up('md')]: {
//     backgroundColor : green[500]

//   },
//   [theme.breakpoints.up('lg')]: {
//     backgroundColor : green[100]

//   },
// }));

const NavBar = () => {

  return (
    // <Breakpoints>
    <AppBar 
      position="relative"
      elevation={5}
      sx={{
        height: '7rem',
        backgroundColor: green[400],
        margin: '.5rem',
        // maxWidth: 1000,
        mx: 'auto',
        borderRadius: 1,
        display: 'flex',
        justifyContent: 'center',
        alignItems: 'left'
      }}
    >
      <Toolbar>
        <List
          sx={{
            display: 'flex',
            flexDirection: 'row',
            
          }}
        >
          {pages.map((page) => (
              <ListItem key={page}>
                <Typography
                  variant="h6" 
                  textAlign="center"

                >
                <Link 
                  href={page.path}
                  color='primary'
                  sx={{
                    textDecoration: 'none',
                    transition: '2',
                    '&:hover': {
                      color: green[200]
                    },
                  }}
                >
                  {page.name}
                </Link>
                </Typography>
              </ListItem>
            ))}
        </List>
      </Toolbar>
    </AppBar>
    // </Breakpoints>
  );
};
export default NavBar;
