import React from 'react';
import {
  AppBar,
  Toolbar, 
  Typography,
  List,
  ListItem,
  Link
} from '@mui/material';
import { 
    green,
    // grey
} from '@mui/material/colors';

const pages = [
  {
    name: 'About',
    path: '/about'
  },
  {
    name: 'Work',
    path: '/work'
  },
  {
    name: 'Contact',
    path: '/contact'
  }
]

const navbar = {
  display: 'flex',
  mx: 'auto',
  backgroundColor : {
    xs: green[100],
    sm: green[300],
    md: green[500],
    lg: green[700]
  }
}

const navLinks = {
  textDecoration: 'none',
  color: 'primary',
    '&:hover': {
      color: 'secondary'
    },
  fontSize : {
    xs: 16,
    md: 20
  }
}

const NavBar = () => {

  return (
    <AppBar 
      position="relative"
      elevation={5}
      sx={navbar}
    >
      <Toolbar>
        <List sx={{ display: 'flex' }}>
          {pages.map((page) => (
              <ListItem key={page}>
                <Typography>
                  <Link 
                    href={page.path}
                    sx={navLinks}
                  >
                    {page.name}
                  </Link>
                </Typography>
              </ListItem>
            ))}
        </List>
      </Toolbar>
    </AppBar>
  );
};
export default NavBar;
