import React from 'react';
import {
  AppBar,
  Toolbar, 
  Typography,
  List,
  ListItem,
  Link
} from '@mui/material';
import { 
  deepPurple,
  // green,
    // grey
} from '@mui/material/colors';

const pages = [
  {
    name: 'About',
    path: '/about'
  },
  {
    name: 'Work',
    path: '/work'
  },
  {
    name: 'Contact',
    path: '/contact'
  }
]

const navbar = {
  display: 'flex',
  mx: 'auto',
  height: '15vh',
  backgroundColor : deepPurple[900]
}

const navLinks = {
  textDecoration: 'none',
  color: 'primaryText',
    '&:hover': {
      color: 'secondary'//not working
    },
  fontSize : {
    xs: 16,
    md: 20
  }
}

const NavBar = () => {

  return (
    <AppBar 
      position="relative"
      elevation={3}
      sx={navbar}
    >
      <Toolbar>
        <List 
          sx={{ 
            display: 'flex',
            paddingTop: '4vh' 
          }}>
          {pages.map((page) => (
              <ListItem key={page}>
                <Typography>
                  <Link 
                    href={page.path}
                    sx={navLinks}
                  >
                    {page.name}
                  </Link>
                </Typography>
              </ListItem>
            ))}
        </List>
      </Toolbar>
    </AppBar>
  );
};
export default NavBar;
